// If we have any approvals, reset it

function resetApprovals() {
	var grGroup = new GlideRecord('sysapproval_group');
	grGroup.addQuery('parent', current.sys_id);
	grGroup.query();
	while (grGroup.next()) {
		grGroup.setValue('approval', 'cancelled');
		grGroup.update();
	}
	
	var grApp = new GlideRecord('sysapproval_approver');
	var qc = grApp.addQuery('sysapproval', current.sys_id);
	qc.addOrCondition('document_id', current.sys_id);
	grApp.query();
	while (grApp.next()) {
		grApp.setValue('state', 'cancelled');
		grApp.update();
	}
}

resetApprovals();

